CREATE OR REPLACE FUNCTION obter_se_fornec_regra_mat_cot ( nr_cot_compra_p bigint, cd_cnpj_p text) RETURNS varchar AS $body$
DECLARE

 
cd_estabelecimento_w		smallint;
cd_material_w			integer;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_gerar_w			varchar(1) := 'N';		
			 
c01 CURSOR FOR 
SELECT	cd_material 
from	cot_compra_item 
where	nr_cot_compra	= nr_cot_compra_p 
and	coalesce(ie_situacao,'A')	= 'A';

c02 CURSOR FOR 
SELECT	coalesce(ie_gerar,'S') 
from	regra_cotacao_mat_fornec 
where	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w 
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w 
and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w 
and	coalesce(cd_material, cd_material_w)			= cd_material_w 
and	substr(obter_dados_pf_pj(null,cd_cgc_fornecedor,'S'),1,1)	= 'A' 
and	cd_cgc_fornecedor = cd_cnpj_p 
and	((coalesce(cd_estabelecimento::text, '') = '') or 
	(cd_estabelecimento IS NOT NULL AND cd_estabelecimento::text <> '' AND cd_estabelecimento = cd_estabelecimento_w)) 
order by 
	coalesce(cd_material, 0), 
	coalesce(cd_classe_material, 0), 
	coalesce(cd_subgrupo_material, 0), 
	coalesce(cd_grupo_material, 0);


BEGIN 
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	cot_compra 
where	nr_cot_compra = nr_cot_compra_p;
 
open C01;
loop 
fetch C01 into	 
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	cd_grupo_material, 
		cd_subgrupo_material, 
		cd_classe_material 
	into STRICT	cd_grupo_material_w, 
		cd_subgrupo_material_w, 
		cd_classe_material_w 
	from	estrutura_material_v 
	where 	cd_material	= cd_material_w;
		 
	open C02;
	loop 
	fetch C02 into	 
		ie_gerar_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		ie_gerar_w := ie_gerar_w;
		end;
	end loop;
	close C02;	
 
	end;	
end loop;
close C01;
 
return	ie_gerar_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
