CREATE OR REPLACE FUNCTION obter_se_funcao_lib_cliente ( cd_estabelecimento_p bigint, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_funcao_liberada_w	varchar(1);


BEGIN
Select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_funcao_liberada_w
from	funcao_desenv
where	cd_cgc_cliente = obter_cgc_estabelecimento(cd_estabelecimento_p)
