CREATE OR REPLACE FUNCTION obter_se_funcao_tasy (ds_form_p text) RETURNS varchar AS $body$
DECLARE


ie_funcao_tasy_w	varchar(1)	:= 'N';
ds_form_w		varchar(10);


BEGIN

if (ds_form_p IS NOT NULL AND ds_form_p::text <> '') then

	ds_form_w	:= upper(replace(ds_form_p,'_',null));

	select	coalesce(max('S'),'N')
	into STRICT	ie_funcao_tasy_w
	from	funcao a
	where	upper(replace(a.ds_form,'_',null))	= ds_form_w;

end if;

return	ie_funcao_tasy_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
