CREATE OR REPLACE FUNCTION obter_se_funcionario_estab (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cont_w			bigint;
ie_funcionario_w	varchar(255);


BEGIN

select	count(*)
into STRICT	cont_w
from	pessoa_fisica_estab
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (cont_w > 0) then
	select	coalesce(max(ie_funcionario), 'N')
	into STRICT	ie_funcionario_w
	from	pessoa_fisica_estab
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
else
	select	ie_funcionario
	into STRICT	ie_funcionario_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
end if;

return	ie_funcionario_w;

end;
$body$
LANGUAGE PLPGSQL
