CREATE OR REPLACE FUNCTION obter_se_func_assistencial (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);

BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	pessoa_fisica_classif_func a,
	classif_pf_func b
where	a.nr_seq_classif = b.nr_sequencia
and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
and	b.ie_classif_assist = 'S';


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
