SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_fx_etaria_regra (nr_atendimento_p text, cd_setor_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_idade_maxima_w  	bigint;
qt_idade_minima_w  	bigint;
qt_idade_paciente_w	bigint;
qt_existe_w		bigint;
ie_achou_w		varchar(1) := 'N';
qt_visita_w	        bigint;


c01 CURSOR FOR

SELECT	coalesce(qt_idade_maxima,0),
	coalesce(qt_idade_minima,0)
from 	regra_visita_faixa_etaria
where	cd_setor_atendimento = cd_setor_p;



BEGIN

select	coalesce(count(*),0)
into STRICT	qt_existe_w
from    regra_visita_faixa_etaria;

select  coalesce(count(*),0)
into STRICT    qt_visita_w
from	atendimento_acompanhante
where	nr_atendimento = nr_atendimento_p
and	coalesce(dt_saida::text, '') = '';

if (qt_existe_w > 0) and (qt_visita_w > 0) then

	select  coalesce(substr(obter_idade_pf(obter_pessoa_atendimento(nr_atendimento_p,'C'),clock_timestamp(),'A'),1,255),0)
	into STRICT 	qt_idade_paciente_w
	;

	open C01;
	loop
	fetch C01 into
		qt_idade_maxima_w,
		qt_idade_minima_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if  (qt_idade_maxima_w IS NOT NULL AND qt_idade_maxima_w::text <> '' AND qt_idade_minima_w IS NOT NULL AND qt_idade_minima_w::text <> '') and (ie_achou_w = 'N') then

		select   coalesce(max('S'),'N')
		into STRICT	 ds_retorno_w
		
		where	 qt_idade_paciente_w between qt_idade_minima_w and qt_idade_maxima_w;

		if (ds_retorno_w = 'S') then
			ie_achou_w	:=	'S';
		end if;
		end if;
		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
