CREATE OR REPLACE FUNCTION obter_se_gap_aprovado (nr_seq_gap_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2);
dt_aprovacao_w	latam_gap.dt_aprovacao%type;


BEGIN

select	dt_aprovacao
into STRICT	dt_aprovacao_w
from	latam_gap
where	nr_sequencia = nr_seq_gap_p;


if (coalesce(dt_aprovacao_w::text, '') = '') then
	begin
		ds_retorno_w := 'N';
	end;
else
	begin
		ds_retorno_w := 'S';
	end;
end if;

return	ds_retorno_w;

end;
$body$
