CREATE OR REPLACE FUNCTION obter_se_gasoterapia_pend ( nr_atendimento_p bigint, ie_chamada_p text default 'P') RETURNS varchar AS $body$
DECLARE


ie_retorno_w	char(1)	:= 'N';
ie_status_w		varchar(1);
dt_inicio_w		timestamp;
			

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	if (coalesce(ie_chamada_p,'P') = 'P') then-- Pacientes
		ie_status_w := 'I';
	elsif (coalesce(ie_chamada_p,'P') = 'G') then-- Gestao
		ie_status_w := 'N';
	end if;
	/*SO-2225714*/

	dt_inicio_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp() - interval '5 days');
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_medica a,
			prescr_gasoterapia b
	where	a.nr_prescricao = b.nr_prescricao
	and		a.nr_atendimento = nr_atendimento_p
	and		a.dt_validade_prescr > dt_inicio_w
	and		obter_status_gasoterapia(b.nr_sequencia,'C') not in ('P', 'T', 'TE', 'S', ie_status_w)
	and   	obter_se_gas_hor_susp(b.nr_prescricao, b.nr_sequencia) = 'N'  LIMIT 1;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
