SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_gas_hor_susp ( nr_prescricao_p bigint, nr_seq_gasoterapia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2) := 'N';
ie_verifica_w	bigint;

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_gasoterapia_p IS NOT NULL AND nr_seq_gasoterapia_p::text <> '') then
	-- Verifica se existe algum horario especial
	select	count(*)
	into STRICT	ie_verifica_w
	from	prescr_gasoterapia_hor
	where 	nr_seq_gasoterapia = nr_seq_gasoterapia_p
	and 	nr_prescricao	= nr_prescricao_p
	and	ie_horario_especial  = 'S';
	-- Se houver:
	if (ie_verifica_w > 0) then
		-- Verifica se  não esta suspenso
		select	count(*)
		into STRICT 	ie_verifica_w
		from	prescr_gasoterapia_hor
		where 	nr_seq_gasoterapia = nr_seq_gasoterapia_p
		and 	nr_prescricao = nr_prescricao_p
		and 	coalesce(dt_suspensao::text, '') = ''
		and	coalesce(dt_fim_horario::text, '') = ''
		and	ie_horario_especial = 'S';
		if (ie_verifica_w > 0) then
			-- Verifica se existe algum horario aprazado que não está suspenso
			select	count(*)
			into STRICT 	ie_verifica_w
			from	prescr_gasoterapia_hor
			where 	nr_seq_gasoterapia = nr_seq_gasoterapia_p
			and 	nr_prescricao = nr_prescricao_p
			and 	coalesce(dt_suspensao::text, '') = ''
			and	coalesce(dt_fim_horario::text, '') = ''
			and	ie_horario_especial = 'S';
			if (ie_verifica_w > 0) then
				ds_retorno_w := 'N';
			else
				ds_retorno_w := 'S';
			end if;
		else
			ds_retorno_w := 'S';
		end if;
	else
		-- Verifica se existe algum horário não suspenso
		select	count(*)
		into STRICT 	ie_verifica_w
		from	prescr_gasoterapia_hor
		where 	nr_seq_gasoterapia = nr_seq_gasoterapia_p
		and 	nr_prescricao = nr_prescricao_p
		and 	coalesce(dt_suspensao::text, '') = ''
		and	coalesce(dt_fim_horario::text, '') = ''
		and	ie_horario_especial = 'N';
		if (ie_verifica_w > 0) then
			ds_retorno_w := 'N';
		else
			ds_retorno_w := 'S';
		end if;
	end if;
	--return ds_retorno_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
