CREATE OR REPLACE FUNCTION obter_se_generico_kit ( cd_material_kit_p bigint, cd_material_p bigint, cd_kit_material_p bigint, nr_seq_comp_p bigint) RETURNS varchar AS $body$
DECLARE


cd_generico_w		material.cd_material%type;
cd_generico_kit_w	material.cd_material%type;
ds_retorno_w		varchar(1);


BEGIN

cd_generico_w := obter_material_generico(cd_material_p);
cd_generico_kit_w := obter_material_generico(cd_material_kit_p);

if (cd_generico_w = cd_generico_kit_w) then
	ds_retorno_w := 'S';
else
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	componente_kit_opcao
	where	obter_material_generico(cd_material) = cd_generico_w
	and	cd_kit_material = cd_kit_material_p
	and	nr_seq_componente = nr_seq_comp_p;
end if;

return	ds_retorno_w;

end;
$body$
