CREATE OR REPLACE FUNCTION obter_se_generico_pda_adep (cd_material_p bigint, cd_mat_comparacao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_mat_generico_w			integer;
cd_mat_generico_subst_w		integer;
ie_generico_igual_w			varchar(1) := 'N';
cd_mat_estoque_w			integer;



BEGIN

select	coalesce(max(cd_material_generico),0)
into STRICT	cd_mat_generico_w
from	material
where	cd_material = cd_material_p;

select	coalesce(max(cd_material_generico),9999)
into STRICT	cd_mat_generico_subst_w
from	material
where	cd_material = cd_mat_comparacao_p;

if (cd_mat_generico_w = cd_mat_generico_subst_w) then
	return 'S';
end if;

cd_mat_estoque_w	:= coalesce(obter_dados_material(cd_material_p,'EST'),0);
cd_mat_generico_w	:= coalesce(obter_dados_material(cd_material_p,'GEN'),9999);


if (cd_mat_estoque_w = cd_mat_comparacao_p) or (cd_mat_generico_w = cd_mat_comparacao_p) then
	return 'S';
end if;

return	'N';

end;
$body$
