CREATE OR REPLACE FUNCTION obter_se_gener_igual_pharm (cd_material_p bigint, cd_mat_substituto_p bigint) RETURNS varchar AS $body$
DECLARE


cd_mat_generico_w		material.cd_material%type;
cd_mat_generico_subst_w	material.cd_material%type;
cd_mat_estoque_w		material.cd_material%type;



BEGIN

if (cd_material_p = cd_mat_substituto_p) then
	return 'S';
end if;

select	coalesce(max(cd_material_generico),0)
into STRICT	cd_mat_generico_w
from	material
where	cd_material = cd_material_p;

select	coalesce(max(cd_material_generico),0)
into STRICT	cd_mat_generico_subst_w
from	material
where	cd_material = cd_mat_substituto_p;

if	(cd_mat_generico_w = cd_mat_generico_subst_w AND cd_mat_generico_w > 0) then
	return 'S';
else

	cd_mat_estoque_w		:= coalesce(obter_dados_material(cd_material_p,'EST'),0);
	cd_mat_generico_subst_w	:= coalesce(obter_dados_material(cd_material_p,'GEN'),9999);

	if (cd_mat_estoque_w = cd_mat_substituto_p) or (cd_mat_generico_subst_w = cd_mat_substituto_p) then
		return 'S';
	end if;

end if;

return	'N';

end;
$body$
