SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_gerando_versao () RETURNS varchar AS $body$
DECLARE


ie_status_aplicacao_w varchar(1);
qt_reg_w	bigint;
ds_retorno_w	varchar(1);


BEGIN
ds_retorno_w := 'N';

select 	ie_status_aplicacao
into STRICT	ie_status_aplicacao_w
from 	aplicacao_tasy
where 	cd_aplicacao_tasy = 'Tasy';

select	count(*)
into STRICT	qt_reg_w
from	v$session
where	audsid = (
	SELECT userenv('sessionid') )
and (upper(machine) like '%WHEBD12%' or upper(machine) like '%WHEBT13%');

if ( ie_status_aplicacao_w = 'U' ) and (qt_reg_w = 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
