CREATE OR REPLACE FUNCTION obter_se_gerar_conta_partic (cd_estabelecimento_p bigint, nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
nr_seq_protocolo_w	bigint;
count_w			bigint;


BEGIN 
ds_retorno_w	:= 'S';
 
select	nr_seq_protocolo 
into STRICT	nr_seq_protocolo_w 
from	conta_paciente 
where	nr_interno_conta	= nr_interno_conta_p;
 
select	count(*) 
into STRICT	count_w 
from	portador b, 
	titulo_receber a 
where	a.cd_tipo_portador		= b.cd_tipo_portador 
and	a.cd_portador			= b.cd_portador 
and	coalesce(b.ie_conta_partic_ret,'S')	= 'N' 
and (a.nr_interno_conta		= nr_interno_conta_p or 
	a.nr_seq_protocolo		= nr_seq_protocolo_w);
 
if (count_w > 0) then 
	ds_retorno_w	:= 'N';
end if;
 
return 	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
