CREATE OR REPLACE FUNCTION obter_se_gerar_etapa_ck ( nr_seq_etapa_p bigint, cd_empresa_p bigint, ie_sexo_p text, qt_idade_p bigint, nr_seq_tipo_checkup_p bigint default null) RETURNS varchar AS $body$
DECLARE

qt_reg_w		bigint;
nr_seq_tipo_checkup_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	ETAPA_CHECKUP_ITEM
where	nr_seq_etapa	= nr_seq_etapa_p;

if (qt_reg_w	>0) then

	nr_seq_tipo_checkup_w := coalesce(nr_seq_tipo_checkup_p,0);

	select	count(*)
	into STRICT	qt_reg_w
	from	ETAPA_CHECKUP_ITEM a
	where	a.nr_seq_etapa	= nr_seq_etapa_p
	and	((coalesce(ie_sexo::text, '') = '') or (ie_sexo = ie_sexo_p))
	and	qt_idade_p between coalesce(QT_IDADE_MIN,0) and coalesce(QT_IDADE_MAX,999)
	and	Obter_se_item_checkup_lib(a.nr_sequencia,cd_empresa_p,nr_seq_tipo_checkup_w)	= 'S';

	if (qt_reg_w	= 0) then
		return 'N';
	end if;


end if;


return	'S';

end;
