SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_gerar_item_adep (ie_tipo_item_p text, nr_prescricao_p bigint, nr_seq_item_p bigint, nr_agrupamento_p bigint, ie_gerar_kit_p text) RETURNS varchar AS $body$
DECLARE


ie_gerar_adep_w	varchar(1) := 'S';
qt_w		bigint;


BEGIN
if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (nr_agrupamento_p IS NOT NULL AND nr_agrupamento_p::text <> '') then

	if (ie_tipo_item_p = 'M') then

		/* verificar item composto */

		select	count(*)
		into STRICT	qt_w
		from	prescr_material
		where	nr_prescricao		= nr_prescricao_p
		and	nr_agrupamento		= nr_agrupamento_p
		and	ie_item_superior	= 'S'
		and	ie_agrupador		= 1
	    and coalesce(dt_suspensao::text, '') = ''
	    and coalesce(nr_seq_substituto::text, '') = '';

		if (coalesce(qt_w,0) = 0) then
			select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
			into STRICT	ie_gerar_adep_w
			from	prescr_material
			where	nr_prescricao		= nr_prescricao_p
			and	nr_sequencia		< nr_seq_item_p
			and	nr_agrupamento		= nr_agrupamento_p
			and	ie_agrupador		= 1
			and coalesce(nr_seq_substituto::text, '') = '';
		else
			select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
			into STRICT	ie_gerar_adep_w
			from	prescr_material
			where	nr_prescricao		= nr_prescricao_p
			and	nr_sequencia		<> nr_seq_item_p
			and	ie_item_superior	= 'S'
			and	nr_agrupamento		= nr_agrupamento_p
			and	ie_agrupador		= 1
		    and coalesce(dt_suspensao::text, '') = ''
		    and coalesce(nr_seq_substituto::text, '') = '';
		end if;

		/* verificar kit */

		if (ie_gerar_adep_w = 'S') and (ie_gerar_kit_p = 'N') then

			select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
			into STRICT	ie_gerar_adep_w
			from	prescr_material
			where	nr_prescricao		= nr_prescricao_p
			and	nr_sequencia		= nr_seq_item_p
			--and	ie_origem_inf		= 'K'
			--and	nr_seq_kit		is not null;
			and	(cd_kit_material IS NOT NULL AND cd_kit_material::text <> '');

		end if;

	end if;

end if;

return ie_gerar_adep_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
