CREATE OR REPLACE FUNCTION obter_se_gerar_item_plt (ie_tipo_item_p text, nr_prescricao_p bigint, nr_seq_item_p bigint, nr_agrupamento_p bigint, ie_item_superior_P text, nr_seq_kit_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gerar_plt_w	varchar(1) := 'S';
qt_w			bigint;


BEGIN
if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (nr_agrupamento_p IS NOT NULL AND nr_agrupamento_p::text <> '') then

	if (ie_tipo_item_p = 'M') then

		/* verificar item composto */

		if (ie_item_superior_p = 'S') then
			ie_gerar_plt_w := 'S';
		elsif (nr_seq_kit_p IS NOT NULL AND nr_seq_kit_p::text <> '') then
			ie_gerar_plt_w := 'N';
		else

			select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
			into STRICT	ie_gerar_plt_w
			from	prescr_material
			where	nr_prescricao	= nr_prescricao_p
			and	nr_sequencia		< nr_seq_item_p
			and	nr_agrupamento		= nr_agrupamento_p
			and	ie_agrupador		= 1
			and	(nr_seq_kit IS NOT NULL AND nr_seq_kit::text <> '');

		end if;

	end if;

end if;

return ie_gerar_plt_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
