CREATE OR REPLACE FUNCTION obter_se_gerar_prescr_agenda ( cd_tipo_agenda_p bigint, cd_agenda_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);


BEGIN 
 
if (cd_tipo_agenda_p IS NOT NULL AND cd_tipo_agenda_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ds_retorno_w 
	from	agenda a, 
		agenda_consulta b 
	where	a.cd_agenda		= b.cd_agenda 
	and	a.cd_tipo_agenda	= cd_tipo_agenda_p 
	and	a.cd_agenda		= cd_agenda_p 
	and	b.nr_sequencia		= nr_seq_agenda_p 
	and	(b.cd_procedimento IS NOT NULL AND b.cd_procedimento::text <> '') 
	and	(b.ie_origem_proced IS NOT NULL AND b.ie_origem_proced::text <> '');
 
	if (ds_retorno_w = 'N') then 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_retorno_w 
		from	agenda_consulta_proc 
		where	nr_seq_agenda = nr_seq_agenda_p 
		and	(cd_procedimento IS NOT NULL AND cd_procedimento::text <> '') 
		and 	(ie_origem_proced IS NOT NULL AND ie_origem_proced::text <> '');
	end if;
end if;
 
return	ds_retorno_w;
 
end;
