CREATE OR REPLACE FUNCTION obter_se_gerar_prescr_ageserv (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gerar_w		varchar(1) := 'N';
qt_proc_w		bigint;
qt_proc_adic_w	bigint;


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	/* obter procedimento agenda */

	select	count(*)
	into STRICT	qt_proc_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	/* obter procedimento adicional agenda */

	select	count(*)
	into STRICT	qt_proc_adic_w
	from	agenda_consulta_proc
	where	nr_seq_agenda = nr_seq_agenda_p;

	if (qt_proc_w > 0) or (qt_proc_adic_w > 0) then
		ie_gerar_w := 'S';
	end if;
end if;

return ie_gerar_w;

end;
