CREATE OR REPLACE FUNCTION obter_se_gerar_turno_agecons ( dt_inicio_vigencia_p timestamp, ie_frequencia_p text, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_gerar_turno_w	varchar(1);
qt_dias_turno_w	bigint;


BEGIN

if (dt_inicio_vigencia_p IS NOT NULL AND dt_inicio_vigencia_p::text <> '') and (ie_frequencia_p IS NOT NULL AND ie_frequencia_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then

	if (ie_frequencia_p = 'D') then /* diaria */
		ie_gerar_turno_w := 'S';

	elsif (ie_frequencia_p = 'S') then /* semanal */
		select	obter_dias_entre_datas(dt_inicio_vigencia_p,dt_agenda_p)
		into STRICT	qt_dias_turno_w
		;

		select	CASE WHEN mod(qt_dias_turno_w,7)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_gerar_turno_w
		;

	elsif (ie_frequencia_p = 'Q') then /* quinzenal */
		select	obter_dias_entre_datas(dt_inicio_vigencia_p,dt_agenda_p)
		into STRICT	qt_dias_turno_w
		;

		select	CASE WHEN mod(qt_dias_turno_w,14)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_gerar_turno_w
		;

	elsif (ie_frequencia_p = 'M') then /* mensal */
		select	obter_dias_entre_datas(dt_inicio_vigencia_p,dt_agenda_p)
		into STRICT	qt_dias_turno_w
		;

		select	CASE WHEN mod(qt_dias_turno_w,30)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_gerar_turno_w
		;

	end if;

else

	ie_gerar_turno_w := 'S';

end if;

return ie_gerar_turno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
