CREATE OR REPLACE FUNCTION obter_se_gerar_turno_semana ( dt_inicio_vigencia_p timestamp, ie_frequencia_p text, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_gerar_turno_w		varchar(1);
qt_dias_turno_w			bigint;
dia_agenda_vigencia_w	bigint;
dia_inicio_vigencia_w	bigint;


BEGIN

if (dt_inicio_vigencia_p IS NOT NULL AND dt_inicio_vigencia_p::text <> '') and (ie_frequencia_p IS NOT NULL AND ie_frequencia_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	
	SELECT obter_cod_dia_semana(dt_inicio_vigencia_p)
	INTO STRICT dia_inicio_vigencia_w
	;
	
	SELECT obter_cod_dia_semana(dt_agenda_p)
	INTO STRICT dia_agenda_vigencia_w
	;

	select	obter_dias_entre_datas(dt_inicio_vigencia_p - dia_inicio_vigencia_w,dt_agenda_p - dia_agenda_vigencia_w)
	into STRICT	qt_dias_turno_w
	;
	
	if (ie_frequencia_p = 'D') then
		ie_gerar_turno_w := 'S';
	elsif (ie_frequencia_p = 'S') then
		ie_gerar_turno_w := 'S';
	
	elsif (ie_frequencia_p = 'Q') then
	/* Gerar a cada 2 semanas */

		select	CASE WHEN mod(qt_dias_turno_w,14) - mod(qt_dias_turno_w,7)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_gerar_turno_w
		;
		
	elsif (ie_frequencia_p = 'M') then
	/* Gerar a cada 4 semanas (nao fecha 30 dias) */

		select	CASE WHEN mod(qt_dias_turno_w,28) - mod(qt_dias_turno_w,7)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_gerar_turno_w
		;
		
	end if;

else
	ie_gerar_turno_w := 'S';
end if;

return ie_gerar_turno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
