CREATE OR REPLACE FUNCTION obter_se_gera_ato_ipasgo ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, cd_tipo_fatura_p bigint, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gerar_ato_w			varchar(15) := 'N';
cd_area_procedimento_w		bigint;
cd_especialidade_w		bigint;
cd_grupo_proc_w			bigint;

C01 CURSOR FOR
	SELECT	coalesce(b.ie_gera_ato,'N')
	from	fatur_tipo_fatura a,
		fatur_tipo_fatura_estrut b
	where	coalesce(b.cd_procedimento,coalesce(cd_procedimento_p,0))		= coalesce(cd_procedimento_p,0)
	and	coalesce(b.ie_origem_proced,coalesce(ie_origem_proced_p,0)) 	= coalesce(ie_origem_proced_p,0)
	and	coalesce(b.cd_area_procedimento,cd_area_procedimento_w)	= cd_area_procedimento_w
	and	coalesce(b.cd_especialidade,cd_especialidade_w)		= cd_especialidade_w
	and	coalesce(b.cd_grupo_proc,cd_grupo_proc_w)			= cd_grupo_proc_w
	and	coalesce(b.ie_tipo_atendimento, ie_tipo_atendimento_p)		= ie_tipo_atendimento_p
	and	coalesce(b.cd_setor_atendimento, cd_setor_atendimento_p)	= cd_setor_atendimento_p
	and	a.cd_estabelecimento 				= cd_estabelecimento_p
	and	b.nr_seq_tipo_fatura	= a.nr_sequencia
	and	a.cd_tipo_fatura 		= cd_tipo_fatura_p
	and	a.ie_situacao 		= 'A'
	order by	coalesce(b.cd_procedimento,0),
		coalesce(b.cd_grupo_proc,0),
		coalesce(b.cd_especialidade,0),
		coalesce(b.cd_area_procedimento,0),
		coalesce(ie_tipo_atendimento,0);


BEGIN

if (cd_procedimento_p > 0) and (ie_origem_proced_p > 0) then
	begin
	select	cd_area_procedimento,
		cd_especialidade,
		cd_grupo_proc
	into STRICT	cd_area_procedimento_w,
		cd_especialidade_w,
		cd_grupo_proc_w
	from	estrutura_procedimento_v
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced= ie_origem_proced_p;

	open C01;
	loop
	fetch C01 into
		ie_gerar_ato_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
	end;
end if;

return	ie_gerar_ato_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
