CREATE OR REPLACE FUNCTION obter_se_gera_ciclo_onc_js ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text, nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';
qt_registro_w		bigint;


BEGIN

if (ie_opcao_p = 'S') then
	begin
		select 	count(*)
		into STRICT	qt_registro_w
		from 	can_loco_regional
		where 	nr_atendimento = nr_atendimento_p;

		if (qt_registro_w = 0) then
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(342977); -- É preciso registrar o loco regional antes de gerar o ciclo para este atendimento. Parâmetro [974]
		end if;
	end;

elsif (ie_opcao_p = 'C') then
	begin
		select 	count(*)
		into STRICT	qt_registro_w
		from 	can_loco_regional
		where 	cd_pessoa_fisica = cd_pessoa_fisica_p;

		if (qt_registro_w = 0) then
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(342978); -- É preciso registrar o loco regional antes de gerar o ciclo para este paciente. Parâmetro [974]
		end if;
	end;

elsif (ie_opcao_p = 'D') then
	begin
		select 	count(*)
		into STRICT	qt_registro_w
		from 	diagnostico_doenca
		where 	nr_atendimento = nr_atendimento_p;

		if (qt_registro_w = 0) then
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(342979); -- É preciso registrar o CID antes de gerar o ciclo para este paciente. Parâmetro [974]
		end if;
	end;

elsif (ie_opcao_p = 'L') then
	begin
		select	count(*)
		into STRICT	qt_registro_w
		from	paciente_setor
		where	(NR_SEQ_LOCO_REGIONAL IS NOT NULL AND NR_SEQ_LOCO_REGIONAL::text <> '')
		and	nr_seq_paciente = nr_seq_paciente_p;

		if (qt_registro_w = 0) then
			ds_retorno_w	:= wheb_mensagem_pck.get_texto(343012); -- Campo "Loco regional" da pasta "Protocolo" não foi informado, Deseja continuar?
		end if;
	end;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
