CREATE OR REPLACE FUNCTION obter_se_gera_item_glosa ( cd_convenio_p bigint, cd_glosa_convenio_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255)	:= null;


BEGIN

ds_retorno_w	:= 'S';

if (cd_glosa_convenio_p IS NOT NULL AND cd_glosa_convenio_p::text <> '') then

	select	coalesce(max(ie_gera_glosa), 'S')
	into STRICT	ds_retorno_w
	from	convenio_motivo_glosa
	where	cd_convenio			= cd_convenio_p
	and	to_char(cd_glosa_convenio)	= coalesce(cd_glosa_convenio_p,'x');

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
