CREATE OR REPLACE FUNCTION obter_se_gera_laudo_lme ( cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_sus_paciente_w		integer;
nr_seq_interno_w		bigint;
ie_retorno_w			varchar(1)	:= 'N';
dt_fim_val_apac_w		timestamp;


BEGIN 
 
/* Verifica se o paciente em pelo menos 1 laudo gerado */
 
select	count(*) 
into STRICT	qt_sus_paciente_w 
from	sus_laudo_paciente 
where	coalesce(dt_cancelamento::text, '') = '' 
and	nr_atendimento	in ( 
		SELECT	nr_atendimento 
		from	atendimento_paciente 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	cd_estabelecimento	= cd_estabelecimento_p) 
and	ie_classificacao = 14;
 
/* Se tiver laudo gerado derifica suas propriedades */
 
if (qt_sus_paciente_w > 0) then 
 
	/* Pega o último laudo gerado */
 
	select	max(nr_seq_interno) 
	into STRICT	nr_seq_interno_w 
	from	sus_laudo_paciente 
	where	coalesce(dt_cancelamento::text, '') = '' 
	and	nr_atendimento	in ( 
		SELECT	nr_atendimento 
		from	atendimento_paciente 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
		and	cd_estabelecimento	= cd_estabelecimento_p) 
	and	ie_classificacao = 14 
	order by dt_emissao desc;
	 
	/* Pega a data de fim da apac do laudo */
 
	select	dt_fim_val_apac 
	into STRICT	dt_fim_val_apac_w 
	from	sus_laudo_paciente 
	where	nr_seq_interno		= nr_seq_interno_w;
	 
	/* Caso o laudo ainda não tenha data de fim da apac ou a data seja menor a data atual permite gerar um novo */
 
	if (dt_fim_val_apac_w IS NOT NULL AND dt_fim_val_apac_w::text <> '') and (trunc(dt_fim_val_apac_w) <= trunc(clock_timestamp())) then 
		ie_retorno_w	:= 'S';
	end if;	
	 
else 
	ie_retorno_w	:= 'S';
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_gera_laudo_lme ( cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

