CREATE OR REPLACE FUNCTION obter_se_gera_ordem_medic ( nr_seq_atendimento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(2);
qt_material_w		bigint;
qt_medic_w		bigint;


BEGIN
retorno_w := 'N';

if (ie_opcao_p  = 'N') then
	select	count(*)
	into STRICT	qt_medic_w
	from	paciente_atend_medic b
	where	b.nr_seq_atendimento = nr_seq_atendimento_p;

	if (qt_medic_w > 0) then
		select	count(*)
		into STRICT	qt_material_w
		from	paciente_atend_medic a
		where	a.nr_seq_atendimento 	= nr_seq_atendimento_p
		and	exists (SELECT	1
				from	material b
				where	a.cd_material = b.cd_material
				and	b.ie_mistura 		<> 'N');

		if (qt_material_w = 0) then
			retorno_w := 'S';
		end if;
	else
		retorno_w := 'S';
	end if;

elsif (ie_opcao_p  = 'S') then
	select	count(*)
	into STRICT	qt_material_w
	from	paciente_atend_medic a
	where	a.nr_seq_atendimento 	= nr_seq_atendimento_p
	and	exists (SELECT	1
			from	material b
			where	a.cd_material = b.cd_material
			and	b.ie_mistura 		<> 'N');

	if (qt_material_w > 0) then
		retorno_w := 'S';
	end if;
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
