CREATE OR REPLACE FUNCTION obter_se_gera_ordem (ie_via_aplicacao_p text, cd_intervalo_p text, qt_dose_p bigint default 0, cd_material_p bigint default 0, cd_unidade_medida_p text default '', ie_tipo_ordem_p text default 'T', ie_medicacao_paciente_p text DEFAULT NULL, nr_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE

ie_gerar_w					varchar(1);
qt_dose_consumo_w			double precision;
cd_unidade_medida_consumo_w	varchar(30);	
			


BEGIN

ie_gerar_w := 'N';

select 	max(cd_unidade_medida_consumo)
into STRICT	cd_unidade_medida_consumo_w
from	material
where	cd_material	=	cd_material_p;

if (cd_unidade_medida_consumo_w <> cd_unidade_medida_p) then
	qt_dose_consumo_w := obter_conversao_unid_med_cons(cd_material_p,cd_unidade_medida_p,qt_dose_p);
else
	qt_dose_consumo_w := qt_dose_p;
end if;

if (ie_via_aplicacao_p IS NOT NULL AND ie_via_aplicacao_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_gerar_w
	from	regra_geracao_ordem
	where	coalesce(ie_via_aplicacao,coalesce(ie_via_aplicacao_p,'X')) = coalesce(ie_via_aplicacao_p,'X') 
	and 	coalesce(cd_intervalo,coalesce(cd_intervalo_p,'X')) = coalesce(cd_intervalo_p,'X')
	and		coalesce(qt_dose_consumo_w,0) > coalesce(qt_dose,0)
	and 	((coalesce(ie_somente_onco,'N') = 'N') or (ie_tipo_ordem_p = 'T'))
	and  	(((coalesce(IE_AMBULATORIAL,'N') = 'S') and (Obter_Tipo_Atendimento(nr_atendimento_p) = 7)) or (coalesce(IE_AMBULATORIAL,'N') = 'N')) 
	and 	CASE WHEN coalesce(ie_medicacao_paciente_p,'N')='S' THEN  coalesce(ie_medicacao_paciente, 'N')  ELSE 'S' END  = 'S'
	and (coalesce(coalesce(cd_estabelecimento,obter_estab_atend(nr_atendimento_p)), 0) = coalesce(obter_estab_atend(nr_atendimento_p), 0));

end if;

return	ie_gerar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
