SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_gera_prescr_inconsist (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


cont_w		bigint := 0;
ie_gerar_w	varchar(10);
qt_sem_erro_w	bigint := 0;


BEGIN

select	count(*)
into STRICT	cont_w
from	Prescr_Solucao
where	nr_prescricao	= nr_prescricao_p
and	ie_erro		= 125
and	ie_suspenso	<> 'S';

if (coalesce(cont_w,0) = 0) then

	select	count(*)
	into STRICT	cont_w
	from	Prescr_Procedimento a
	where	nr_prescricao	= nr_prescricao_p
	and	ie_suspenso	<> 'S'
	and	a.ie_erro	= 125
	and	ie_origem_inf	<> 'L'
	and	coalesce(nr_seq_origem::text, '') = '';

	select	count(*)
	into STRICT	qt_sem_erro_w
	from	Prescr_Procedimento a
	where	nr_prescricao	= nr_prescricao_p
	and	ie_suspenso	<> 'S'
	and	a.ie_erro	<> 125
	and	ie_origem_inf	<> 'L'
	and	coalesce(nr_seq_origem::text, '') = '';

	if (coalesce(cont_w,0) = 0) then
		select	count(*)
		into STRICT	cont_w
		From	Material b,
			Prescr_Material a
		where	a.nr_prescricao = nr_prescricao_p
		and	a.ie_suspenso 	<> 'S'
		and	b.ie_situacao	= 'A'
		and	a.ie_origem_inf	<> 'K'
		and	a.ie_agrupador not in (10,11)
		and	a.ie_erro	= 125
		and	a.cd_material 	= b.cd_material;

		select	count(*)
		into STRICT	qt_sem_erro_w
		From	Material b,
			Prescr_Material a
		where	a.nr_prescricao = nr_prescricao_p
		and	a.ie_suspenso 	<> 'S'
		and	b.ie_situacao	= 'A'
		and	a.ie_origem_inf	<> 'K'
		and	a.ie_agrupador not in (10,11)
		and	a.ie_erro	<> 125
		and	a.cd_material 	= b.cd_material;

	end if;

end if;

ie_gerar_w	:= 'N';
if (cont_w	> 0) and (qt_sem_erro_w > 0) then
	ie_gerar_w	:= 'S';
end if;

return	ie_gerar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_gera_prescr_inconsist (nr_prescricao_p bigint) FROM PUBLIC;

