CREATE OR REPLACE FUNCTION obter_se_gera_presc_sae (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


qt_prescr_w bigint;
ie_existe_w  varchar(1) := 'N';

BEGIN

select  count(*)
into STRICT 	qt_prescr_w
from 	pe_prescr_proc a,
	pe_material_proced b
where 	b.nr_seq_proc = a.nr_seq_proc
and 	a.nr_seq_prescr = nr_sequencia_p;

if (coalesce(qt_prescr_w,0) = 0) then
	select 	count(*)
	into STRICT  	qt_prescr_w
	from 	pe_prescr_proc b,
		pe_interv_protocolo a
	where 	b.nr_seq_proc = a.nr_seq_interv
	and	b.nr_seq_prescr = nr_sequencia_p;
end if;

if (coalesce(qt_prescr_w,0) > 0) then
	ie_existe_w := 'S';
end if;


return ie_existe_w;

end;
