CREATE OR REPLACE FUNCTION obter_se_gera_proc_inter_bpa ( cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'S';
qt_regra_w	bigint;


BEGIN

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') then
	select	count(*)
	into STRICT	qt_regra_w
	from	regra_proc_internar_bpa
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	coalesce(ie_situacao,'I')		= 'A';

	if (qt_regra_w	> 0) then
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
