CREATE OR REPLACE FUNCTION obter_se_gera_repasse_plantao (nr_repasse_terceiro_p bigint, nr_seq_tipo_plantao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10) := 'S';
count_w			bigint;


BEGIN

select	count(*)
into STRICT	count_w
from	repasse_terceiro_regra
where	nr_repasse_terceiro	= nr_repasse_terceiro_p
and	(nr_seq_tipo_plantao IS NOT NULL AND nr_seq_tipo_plantao::text <> '');

if (count_w > 0) then

	select	count(*)
	into STRICT	count_w
	from	repasse_terceiro_regra
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p
	and	nr_seq_tipo_plantao	= nr_seq_tipo_plantao_p;

	if (count_w > 0) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
else
	ds_retorno_w	:= 'S';
end if;

return ds_retorno_w;

end;
$body$
