CREATE OR REPLACE FUNCTION obter_se_gera_sae_sis_ter ( NR_SEQUENCIA_P bigint, IE_TIPO_P text) RETURNS varchar AS $body$
DECLARE

			
ds_retorno_w char(1) := 'N';


BEGIN

if (IE_TIPO_P = 'I') then
	
	Select 	coalesce(IE_SISTEMA_TERCEIRO,'N')
	into STRICT	ds_retorno_w
	FROM 	PE_PROCEDIMENTO
	WHERE 	NR_SEQUENCIA = NR_SEQUENCIA_P
	and		coalesce(ie_situacao,'A') = 'A';
	
elsif (IE_TIPO_P = 'A') then

	Select 	coalesce(IE_SISTEMA_TERCEIRO,'N')
	into STRICT	ds_retorno_w
	FROM 	PE_ITEM_EXAMINAR
	WHERE 	NR_SEQUENCIA = NR_SEQUENCIA_P
	and		coalesce(ie_situacao,'A') = 'A';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
