CREATE OR REPLACE FUNCTION obter_se_gera_servico ( nr_seq_servico_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
cd_dieta_w	bigint;

C01 CURSOR FOR
	SELECT	cd_dieta
	from	prescr_dieta
	where	nr_prescricao = nr_prescricao_p
	AND	coalesce(ie_suspenso,'N') <> 'S';


BEGIN
ie_retorno_w := 'N';

open C01;
loop
fetch C01 into
	cd_dieta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_retorno_w
		from	nut_regra_dieta_servico a
		where	nr_seq_servico = nr_seq_servico_p
		AND	cd_dieta = cd_dieta_w
		AND	coalesce(ie_gera_servico,'N') = 'N';

	end if;
	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
