CREATE OR REPLACE FUNCTION obter_se_gerente_lider_grupo ( nm_usuario_p text, nr_seq_grupo_p bigint, nr_seq_gerencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_existe_w 		varchar(1) := 'N';
cd_pessoa_fisica_w 	bigint;


BEGIN 
 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
 
	select 	max(OBTER_PF_USUARIO(nm_usuario_p,'C')) 
	into STRICT 	cd_pessoa_fisica_w 
	;
 
	select 	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
	into STRICT	ie_existe_w 
	from 	grupo_desenvolvimento 
	where 	nm_usuario_lider = nm_usuario_p 
	and  	nr_sequencia = nr_seq_grupo_p;
	 
	if (ie_existe_w = 'N') then 
		begin 
	 
		SELECT 	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT 	ie_existe_w 
		 
		WHERE 	Obter_Gerencia_grupo_desen(nr_seq_grupo_p, 'C') = nr_seq_gerencia_p 
		AND 	OBTER_RESPONSAVEL_GERENCIA(nr_seq_gerencia_p) = cd_pessoa_fisica_w;
		 
		end;
		 
	end if;
	 
	end;
	 
end if;
	 
return	ie_existe_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
