CREATE OR REPLACE FUNCTION obter_se_glicemia_fora_prazo (ie_cig_p text, nr_seq_glicemia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_fora_prazo_w	varchar(1) := 'N';


BEGIN
if (ie_cig_p IS NOT NULL AND ie_cig_p::text <> '') and (nr_seq_glicemia_p IS NOT NULL AND nr_seq_glicemia_p::text <> '') then
	/* cig */

	if (ie_cig_p = 'S') then
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_fora_prazo_w
		from	atendimento_cig
		where	nr_sequencia = nr_seq_glicemia_p
		and	((dt_controle between dt_limite_ant and dt_limite_pos) or (coalesce(dt_limite_ant::text, '') = '' and coalesce(dt_limite_pos::text, '') = ''));
	/* glicemia */

	else
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_fora_prazo_w
		from	atendimento_glicemia
		where	nr_sequencia = nr_seq_glicemia_p
		and	((dt_controle between dt_limite_ant and dt_limite_pos) or (coalesce(dt_limite_ant::text, '') = '' and coalesce(dt_limite_pos::text, '') = ''));
	end if;
end if;

return ie_fora_prazo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
