SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_gqa_pend_liberada ( nr_seq_gqa_pend_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  varchar(1) := 'N';
qt_registro_w  smallint;


BEGIN
  if (nr_seq_gqa_pend_p IS NOT NULL AND nr_seq_gqa_pend_p::text <> '') then
    select  count(*)
    into STRICT  qt_registro_w
    from  gqa_pendencia_prot_lib
    where  nr_seq_protocolo = nr_seq_gqa_pend_p
    and    ie_situacao = 'A';

    if (qt_registro_w > 0) then
      select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END 
      into STRICT  ds_retorno_w
      from  gqa_pendencia_prot_lib
      where  nr_seq_protocolo = nr_seq_gqa_pend_p
      and    ie_situacao = 'A'
      and (coalesce(cd_perfil::text, '') = ''
      or    cd_perfil = wheb_usuario_pck.get_cd_perfil)
      and (coalesce(cd_estabelecimento::text, '') = ''
      or    cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento)
      and (coalesce(cd_setor_atendimento::text, '') = ''
      or    cd_setor_atendimento = wheb_usuario_pck.get_cd_setor_atendimento)
      and (coalesce(cd_especialidade::text, '') = ''
      or    cd_especialidade = obter_especialidade_pf(obter_pf_usuario(wheb_usuario_pck.get_nm_usuario, 'C')));
    else
      ds_retorno_w := 'S';
    end if;
  end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
