CREATE OR REPLACE FUNCTION obter_se_gravida ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
ie_sexo_w	varchar(1);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select	a.ie_sexo
	into STRICT	ie_sexo_w
	from	pessoa_fisica a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ie_sexo_w = 'F') then
		select	max(a.ie_pac_gravida)
		into STRICT	ds_retorno_w
		from	atendimento_gravidez a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
	else
		ds_retorno_w := 'N';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
