CREATE OR REPLACE FUNCTION obter_se_grupo_contem_frase ( nr_seq_grupo_p bigint, ds_frase_p text, ie_tipo_p bigint) RETURNS varchar AS $body$
DECLARE

/* ie _tipo_
 0 - c√≥digo
 1 - frase
*/
ds_retorno_w	varchar(1);

BEGIN
if (coalesce(ds_frase_p::text, '') = '') then
  ds_retorno_w :=  'S';
else
	if (ie_tipo_p = 0) then
		select CASE WHEN count(*)='0' THEN 'N'  ELSE 'S' END
		into STRICT   ds_retorno_w
		from   Protocolo_Descricao
		where  nr_seq_grupo = nr_seq_grupo_p
		and    upper(cd_interno) like('%' || upper(ds_frase_p) || '%');
	elsif (ie_tipo_p = 1) then
		select CASE WHEN count(*)='0' THEN 'N'  ELSE 'S' END
		into STRICT   ds_retorno_w
		from   Protocolo_Descricao
		where  nr_seq_grupo = nr_seq_grupo_p
		and    upper(ds_frase) like('%' ||  upper(ds_frase_p) || '%');
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
