CREATE OR REPLACE FUNCTION obter_se_grupo_lib (nm_usuario_reg_p text) RETURNS varchar AS $body$
DECLARE

	cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
	
	ds_usuario_w		varchar(1000);
	ds_usuarios_lib_w	varchar(1000);
	
	c01 CURSOR FOR 
		SELECT	distinct nm_usuario 
		from	med_permissao a 
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_w 
		or	 	a.nr_seq_grupo in (	SELECT b.nr_seq_grupo 
									from med_grupo_medico b 
									where b.cd_medico = cd_pessoa_fisica_w);	

BEGIN 
	if (nm_usuario_reg_p IS NOT NULL AND nm_usuario_reg_p::text <> '') then 
		select	obter_pessoa_fisica_usuario(wheb_usuario_pck.get_nm_usuario(), 'C') 
		into STRICT	cd_pessoa_fisica_w 
		;
		 
		open c01;
		loop 
			fetch c01 
				into ds_usuario_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
			begin 
				ds_usuarios_lib_w := ds_usuarios_lib_w || ds_usuario_w || ',';				
			end;
		end loop;
		close c01;
	 
		return obter_Se_Contido_char(nm_usuario_reg_p, ds_usuarios_lib_w);
	end if;	
return 'N';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
