CREATE OR REPLACE FUNCTION obter_se_grupo_mat_lib ( nr_seq_grupo_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	varchar(1) := 'S';
cont_w		bigint;


BEGIN

select	count(*)
into STRICT		cont_w
from		grupo_material_rotina_lib
where	nr_seq_grupo	= nr_seq_grupo_p;

if (cont_w > 0) then
	select	count(*)
	into STRICT		cont_w
	from		grupo_material_rotina_lib
	where	nr_seq_grupo	= nr_seq_grupo_p
	and		coalesce(cd_perfil,coalesce(cd_perfil_p,0))	= coalesce(cd_perfil_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0);

	if (cont_w = 0) then
		ie_liberado_w	:= 'N';
	end if;
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
