SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_grupo_prod_perfil (nr_seq_grupo_p bigint, nr_seq_produto_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);
count_w		bigint;
nr_seq_grupo_w	bigint;


BEGIN

ds_retorno_w	:= 'N';
count_w		:= 0;

nr_seq_grupo_w	:= nr_seq_grupo_p;

if (coalesce(nr_seq_produto_p,0) > 0) then /*Buscar o grupo do produto financeiro*/
	select	max(a.nr_seq_grupo_sup)
	into STRICT	nr_seq_grupo_w
	from	grupo_prod_financ a,
		produto_financeiro b
	where	b.nr_seq_grupo		= a.nr_sequencia
	and	b.nr_sequencia		= nr_seq_produto_p;
end if;

if (coalesce(nr_seq_grupo_w,0) > 0) then

	select	count(nr_sequencia)
	into STRICT	count_w
	from	grupo_prod_financ_perfil
	where	nr_seq_grupo	= nr_seq_grupo_w;

	if (count_w > 0) then

		select	count(nr_sequencia)
		into STRICT	count_w
		from	grupo_prod_financ_perfil
		where	nr_seq_grupo		= nr_seq_grupo_w
		and	cd_perfil		= cd_perfil_p
		and	coalesce(ie_acesso,'S')	= 'S';

		if (count_w > 0) then
			ds_retorno_w	:= 'S';
		else
			ds_retorno_w	:= 'N';
		end if;
	else
		ds_retorno_w := 'S';
	end if;
else
	/*Verifica as regras existentes para os perfis onde não existe grupo informado - para definir se o perfil permite consultar por exemplo títulos que não possuem grupo informado*/

	begin
	select	ie_acesso
	into STRICT	ds_retorno_w
	from	grupo_prod_financ_perfil
	where	cd_perfil	= cd_perfil_p
	and	coalesce(nr_seq_grupo::text, '') = ''  LIMIT 1;
	exception
	when no_data_found then
		ds_retorno_w := 'S';
	end;

end if;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
