CREATE OR REPLACE FUNCTION obter_se_grupo_sol_liberado ( cd_perfil_p bigint, nr_seq_grupo_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	varchar(1) := 'S';


BEGIN

select	coalesce(max('N'),'S')
into STRICT	ie_liberado_w
from	grupo_sol_rotina_perfil where		nr_seq_grupo	= nr_seq_grupo_p LIMIT 1;

if (ie_liberado_w = 'N') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_liberado_w
	from	grupo_sol_rotina_perfil where		nr_seq_grupo	= nr_seq_grupo_p
	and		cd_perfil_p		= coalesce(cd_perfil,cd_perfil_p)
	and		cd_estabelecimento_p	= coalesce(cd_estabelecimento,cd_estabelecimento_p) LIMIT 1;

end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
