CREATE OR REPLACE FUNCTION obter_se_grupo_trab_usu_rota ( nr_seq_item_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_ordem_serv_w		bigint;
nr_grupo_trabalho_w		bigint;
ds_retorno_w			varchar(1) := 'S';


BEGIN

if (coalesce(nr_seq_item_p,0) > 0) then
	begin
	select	coalesce(max(nr_seq_ordem_serv),0)
	into STRICT	nr_seq_ordem_serv_w
	from	man_rota_inspecao_item
	where	nr_sequencia = nr_seq_item_p;

	select	coalesce(max(nr_grupo_trabalho),0)
	into STRICT	nr_grupo_trabalho_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_serv_w;

	if (nr_seq_ordem_serv_w > 0) and (nr_grupo_trabalho_w > 0) then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	man_grupo_trab_usuario b,
			man_ordem_servico a
		where   b.nr_seq_grupo_trab = a.nr_grupo_trabalho
		and	a.nr_sequencia = nr_seq_ordem_serv_w
		and	b.nr_seq_grupo_trab = nr_grupo_trabalho_w
		and	b.nm_usuario_param = nm_usuario_p  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
