CREATE OR REPLACE FUNCTION obter_se_guia_atend ( nr_atendimento_p bigint, cd_convenio_p bigint, nr_guia_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);

nr_atendimento_w	bigint;


BEGIN

ie_retorno_w := 'N';

select coalesce(max(nr_atendimento),0)
into STRICT nr_atendimento_w
from atend_categoria_convenio
where nr_atendimento = nr_atendimento_p
  and cd_convenio	= coalesce(cd_convenio_p, cd_convenio)
  and nr_doc_convenio = nr_guia_p;

if (nr_atendimento_w <> 0) then
	ie_retorno_w := 'S';
else
	select coalesce(max(nr_atendimento),0)
	into STRICT nr_atendimento_w
	from autorizacao_convenio
	where nr_atendimento = nr_atendimento_p
	  and cd_convenio	= coalesce(cd_convenio_p, cd_convenio)
	  and cd_autorizacao = nr_guia_p;

	if (nr_atendimento_w <> 0) then
		ie_retorno_w := 'S';
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
