SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_guia_repet_contas (cd_autorizacao_p text, nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_contas_w			varchar(255);
nr_interno_conta_w			bigint;
qt_contas_w			bigint;

c01 CURSOR FOR 
	SELECT	a.nr_interno_conta 
	from	conta_paciente a, 
		conta_paciente_guia b 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	b.cd_autorizacao	= cd_autorizacao_p 
	
union
 	 
	SELECT	a.NR_interno_conta 
	from	conta_paciente_honorario_v a 
	where	nr_seq_protocolo	= nr_seq_protocolo_p 
	and	a.NR_DOC_HONOR_CONV	= cd_autorizacao_p 
	group by a.NR_interno_conta;


BEGIN 
 
ds_contas_w		:= '';
qt_contas_w		:= 0;
 
open	c01;
loop 
fetch	c01 into 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	qt_contas_w	:= qt_contas_w + 1;
 
	ds_contas_w	:= substr(ds_contas_w || to_char(nr_interno_conta_w) || ',',1,255);
 
 
	end;
end loop;
close c01;
 
if (qt_contas_w <= 1) then 
	ds_contas_w	:= '';
end if;
 
return	ds_contas_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
