CREATE OR REPLACE FUNCTION obter_se_habilita_imprimir ( cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1) := 'S';

BEGIN
/* Verifica se habilita o botão imprimir para o setor da prescrição  */

select	coalesce(max(ie_imprimir_rep),'S')
into STRICT		ds_retorno_w
from		setor_atendimento
where	cd_setor_atendimento = cd_setor_atendimento_p
and		cd_estabelecimento = cd_estabelecimento_p;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
