CREATE OR REPLACE FUNCTION obter_se_habilita_regra_sip ( ie_campo text, nr_seq_item_assist sip_item_assistencial.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


retorno_w			varchar(1);


BEGIN

retorno_w := 'N';

if (ie_campo = 'ie_nascido_vivo') then
	-- só pode para o item assistencial Nascidos Vivos
	if (nr_seq_item_assist = 108) then
		retorno_w := 'S';
	end if;

elsif (ie_campo = 'ie_materiais') then
	-- só não pode ser visualizado para internações até nascido vivo
	if (nr_seq_item_assist between 64 and 108) then
		retorno_w := 'N';
	else
		retorno_w := 'S';
	end if;

elsif (ie_campo = 'ie_considerar_conta') then
	-- só não pode ser visualizado para internações até nascido vivo
	if (nr_seq_item_assist between 64 and 108) then
		retorno_w := 'N';
	else
		retorno_w := 'S';
	end if;

elsif (ie_campo = 'ie_considerar_atendimento') then
	-- só não pode ser visualizado para internações até nascido vivo
	if (nr_seq_item_assist between 64 and 108) then
		retorno_w := 'N';
	else
		retorno_w := 'S';
	end if;
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
