CREATE OR REPLACE FUNCTION obter_se_habito_lib ( ie_classificacao_p text, cd_pessoa_fisica_p text, nr_seq_tipo_consulta_p bigint, nr_atendimento_p bigint, nr_seq_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1) := 'S';
qt_existe_lib_w			integer;
qt_idade_w				integer;
qt_idade_meses_w		integer;
cd_setor_atendimento_w  varchar(10);
cd_perfil_w             varchar(10);
cd_estabelecimento_w    varchar(10);
ie_sexo_w               varchar(10);
nr_seq_tipo_consulta_w  MED_TIPO_VICIO_LIBERACAO.nr_seq_consulta_pepa%type := 0;



BEGIN

	select count(1)
	into STRICT qt_existe_lib_w
	from MED_TIPO_VICIO_LIBERACAO a, MED_TIPO_VICIO b
	where a.nr_seq_tipo = b.nr_sequencia
	and b.IE_CLASSIFICACAO = coalesce(ie_classificacao_p, b.IE_CLASSIFICACAO)
    and b.nr_sequencia = coalesce(nr_seq_tipo_p , b.nr_sequencia);


	if (qt_existe_lib_w > 0) then

		qt_idade_w := coalesce(obter_idade_pf(cd_pessoa_fisica_p, clock_timestamp(),'A'), 0);
		qt_idade_meses_w := coalesce(obter_idade_pf(cd_pessoa_fisica_p, clock_timestamp(),'M'), 0);
		cd_setor_atendimento_w := coalesce(obter_setor_atendimento(nr_atendimento_p), 0);
		cd_perfil_w := coalesce(obter_perfil_ativo, 0);
		cd_estabelecimento_w := coalesce(obter_estabelecimento_ativo, 0);
		ie_sexo_w := coalesce(Obter_Sexo_PF(cd_pessoa_fisica_p, 'C'), 'A');
		
		if (nr_seq_tipo_consulta_p > 0) then
			select NR_SEQ_TIPO_CONSULTA
			into STRICT   nr_seq_tipo_consulta_w
			from   ATEND_CONSULTA_PEPA
			where  nr_sequencia = nr_seq_tipo_consulta_p;
		end if;

        select count(*)
        into STRICT qt_existe_lib_w
		from MED_TIPO_VICIO_LIBERACAO a, MED_TIPO_VICIO b
		where a.nr_seq_tipo = b.nr_sequencia
        and b.IE_CLASSIFICACAO = coalesce(ie_classificacao_p, b.IE_CLASSIFICACAO)
        and b.nr_sequencia = coalesce(nr_seq_tipo_p, b.nr_sequencia)
		and cd_setor_atendimento_w = coalesce(a.cd_setor_atendimento, coalesce(cd_setor_atendimento_w, 0))
        and (cd_perfil_w = coalesce(a.cd_perfil, coalesce(cd_perfil_w, 0)) or coalesce(cd_perfil_w, 0) = 0)
        and cd_estabelecimento_w = coalesce(a.cd_estabelecimento, coalesce(cd_estabelecimento_w, 0))
		and nr_seq_tipo_consulta_w = coalesce(a.nr_seq_consulta_pepa, nr_seq_tipo_consulta_w)
		and (coalesce(ie_sexo_w, 'A') = coalesce(coalesce(a.ie_sexo, 'A'), coalesce(ie_sexo_w, 'A')) or a.ie_sexo = 'A')
		and coalesce(qt_idade_w, 0) between coalesce(a.qt_anos_min, 0) and coalesce(a.qt_anos_max,9999)
		and coalesce(qt_idade_meses_w, 0) between coalesce(a.qt_meses_min, 0) and coalesce(a.qt_meses_max,9999);


		if (qt_existe_lib_w > 0) then
			ie_retorno_w := 'S';

		else

			select coalesce(max('S'), 'N') into STRICT ie_retorno_w
			from MED_TIPO_VICIO a
			where a.IE_CLASSIFICACAO = coalesce(ie_classificacao_p , IE_CLASSIFICACAO)
			and a.nr_sequencia = coalesce(nr_seq_tipo_p, a.nr_sequencia)
            and not exists (SELECT (1) from MED_TIPO_VICIO_LIBERACAO b
												where b.nr_seq_tipo = a.nr_sequencia);

		end if;


	end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
