CREATE OR REPLACE FUNCTION obter_se_hab_status_gv ( ie_status_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(1) := 'S';
ie_valor_param_w	varchar(255);

BEGIN
ie_valor_param_w := Obter_param_Usuario(1002, 136, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_valor_param_w);

if (ie_status_p IS NOT NULL AND ie_status_p::text <> '') and (ie_valor_param_w IS NOT NULL AND ie_valor_param_w::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	
	where	obter_se_contido_char(ie_status_p, ie_valor_param_w) = 'S';

end if;

return	ds_retorno_w;

end;
