CREATE OR REPLACE FUNCTION obter_se_ha_comp_dispensavel ( cd_kit_material_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ha_dispensavel_w	varchar(1);
cd_estabelecimento_w	smallint;


BEGIN
select 	wheb_usuario_pck.get_cd_estabelecimento
into STRICT	cd_estabelecimento_w
;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_ha_dispensavel_w
from 	componente_kit
where 	cd_kit_material 	= cd_kit_material_p
and	cd_material	= cd_material_p
and 	ie_dispensavel 	= 'S'
and	((coalesce(cd_estab_regra::text, '') = '') or (cd_estab_regra = cd_estabelecimento_w));

return	ie_ha_dispensavel_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
